i.icon {
    display: inline-block;
    vertical-align: middle;
    font-size: 160%;
    padding: 0 5px;
    font-style: normal;
    line-height: 1;
}

.btnCustom {
	display:inline-block;
	background: #05CD51;
	color: #fff;
	padding: 5px 20px;
	border-radius: 100px;
	font-size:100%;
	box-shadow:inset 0 -3px 0 rgba(0,0,0,.1);
	text-shadow:0 2px 0  rgba(0,0,0,.1);
}
.btnCustom:hover {
	color:#fff;
}
.btnCustom:focus,
.btnCustom:active {
	border:none;
	box-shadow:none;
}
.btnCustom.cekongkir {
	background: #9B59BB;
	color: #fff;
}
.btnCustom.followup {
	background: #05CD51;
	color: #fff;
}
.wp-person a:focus .gravatar, a:focus, a:focus .media-icon img, .button-link:focus {
	box-shadow:none!important;
}

